-- Add cohorts for ALL SAFe 2-day certification classes
-- Pattern: Mon-Tue, Thu-Fri, Sat-Sun (repeating weekly)
-- Date range: Feb 9, 2026 through May 31, 2026
-- Instructors: Deadra, Joe, Marcus (rotating)

-- STEP 1: Check existing schedules
SELECT course_slug, course_name, start_date, end_date, instructor_name, is_weekend
FROM course_schedules
WHERE start_date >= '2026-02-09' AND start_date <= '2026-05-31'
  AND course_type = 'SAFe' AND duration LIKE '%02 days%'
ORDER BY course_slug, start_date;

-- STEP 2: Identify off-pattern dates (NOT Mon-Tue, Thu-Fri, Sat-Sun)
SELECT course_slug, course_name, start_date, end_date,
  CASE
    WHEN EXTRACT(DOW FROM start_date) = 1 THEN 'Monday - CORRECT'
    WHEN EXTRACT(DOW FROM start_date) = 4 THEN 'Thursday - CORRECT'
    WHEN EXTRACT(DOW FROM start_date) = 6 THEN 'Saturday - CORRECT'
    ELSE 'OFF-PATTERN - NEEDS UPDATE'
  END as pattern_status
FROM course_schedules
WHERE start_date >= '2026-02-09' AND start_date <= '2026-05-31'
  AND course_type = 'SAFe' AND duration LIKE '%02 days%'
  AND EXTRACT(DOW FROM start_date) NOT IN (1, 4, 6)
ORDER BY course_slug, start_date;

