-- Fix duplicate dates and add missing cohorts for ALL SAFe 2-day certification classes
-- Pattern: Mon-Tue, Thu-Fri, Sat-Sun (repeating weekly)
-- Date range: Feb 9, 2026 through May 31, 2026
-- Each date pair should appear ONLY ONCE per course

-- STEP 1: Identify duplicate dates (same course on same date pair)
SELECT course_slug, start_date, end_date, COUNT(*) as duplicate_count
FROM course_schedules
WHERE start_date >= '2026-02-09' AND start_date <= '2026-05-31'
  AND course_type = 'SAFe' AND duration LIKE '%02 days%'
GROUP BY course_slug, start_date, end_date
HAVING COUNT(*) > 1
ORDER BY course_slug, start_date;

-- STEP 2: Remove duplicates - keep only the first entry for each course + date pair
DELETE FROM course_schedules
WHERE id IN (
  SELECT id FROM (
    SELECT id, ROW_NUMBER() OVER (PARTITION BY course_slug, start_date, end_date ORDER BY id) as rn
    FROM course_schedules
    WHERE start_date >= '2026-02-09' AND start_date <= '2026-05-31'
      AND course_type = 'SAFe' AND duration LIKE '%02 days%'
  ) t WHERE rn > 1
);

-- STEP 3: Verify duplicates are removed (each course should have only one entry per date pair)
SELECT course_slug, start_date, end_date, COUNT(*) as count
FROM course_schedules
WHERE start_date >= '2026-02-09' AND start_date <= '2026-05-31'
  AND course_type = 'SAFe' AND duration LIKE '%02 days%'
GROUP BY course_slug, start_date, end_date
HAVING COUNT(*) > 1;
